(function () {try {const links = document.querySelectorAll('link[rel="preload"][as="style"]');if (!links || links.length === 0) return;links.forEach((ln) => {try {ln.rel = 'stylesheet';document.documentElement.classList.add('css-ready');} catch (e) {}ln.addEventListener('load', function () {try {this.rel = 'stylesheet';document.documentElement.classList.add('css-ready');} catch (e) {}});if (ln.sheet || ln.getAttribute('data-applied') === '1' || ln.rel === 'stylesheet') {ln.rel = 'stylesheet';document.documentElement.classList.add('css-ready');}setTimeout(() => {if (!(ln.sheet || ln.rel === 'stylesheet')) {const href = ln.getAttribute('href');if (href) {const el = document.createElement('link');el.rel = 'stylesheet';el.href = href;el.addEventListener('load', () => document.documentElement.classList.add('css-ready'));document.head.appendChild(el);ln.setAttribute('data-applied', '1');}}}, 500);});} catch (e) {}})();