function setupScrollAnimations() {const prefersReducedMotion = window.matchMedia('(prefers-reduced-motion: reduce)').matches;if (prefersReducedMotion) {return;}const observerOptions = {threshold: 0.1,rootMargin: '0px 0px -50px 0px'};const observer = new IntersectionObserver((entries) => {entries.forEach(entry => {if (entry.isIntersecting) {entry.target.classList.add('animate-in');}});}, observerOptions);document.querySelectorAll('.animate-on-scroll').forEach(el => {observer.observe(el);});}setupScrollAnimations();if (typeof module === 'object' && module && typeof module.exports === 'object') {module.exports = {setupScrollAnimations};}